package spring.spring_boot_mybatis.service;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import spring.spring_boot_mybatis.domain.Board;import spring.spring_boot_mybatis.dto.BoardDTO;import spring.spring_boot_mybatis.mapper.BoardMapper;import java.util.ArrayList;import java.util.Date;import java.util.List;@Servicepublic class BoardService {    @Autowired    private BoardMapper boardMapper;    public List<BoardDTO> getAllBoards(){        List<Board> boards = boardMapper.findAll();        List<BoardDTO> boardDTOs = new ArrayList<>();        for(Board board:boards){            BoardDTO boardDTO = convertToDto(board);            boardDTOs.add(boardDTO);        }        return boardDTOs;    }    public BoardDTO getBoardByWriter(String writer){        Board board = boardMapper.findByWriter(writer);        return convertToDto(board);    }    public void updateBoard(BoardDTO boardDTO){        Board board = convertToEntity(boardDTO);        boardMapper.update(board);    }    public void deleteBoard(int id){        boardMapper.delete(id);    }    public void createBoard(BoardDTO boardDTO){        Board board = convertToEntity(boardDTO);        boardMapper.insert(board);    }    private BoardDTO convertToDto(Board board){        BoardDTO dto = new BoardDTO();        dto.setId(board.getId());        dto.setTitle(board.getTitle());        dto.setContent(board.getContent());        dto.setWriter(board.getWriter());        dto.setNo((int)(board.getId()+100));        return dto;    }    private Board convertToEntity(BoardDTO dto){        Board board = new Board();        board.setId(dto.getId());        board.setTitle(dto.getTitle());        board.setContent(dto.getContent());        board.setWriter(dto.getWriter());        return board;    }}